// 1. Tanpa Ternari Operator
let value = true
if(value){
    console.log("Ok tanpa Ternary Operator")
}else{
    console.log("Tidak Ok tanpa Ternary Operator")
}

// 2. Ternari Operator
value ? console.log("Ok Ternary Operator") : console.log("Tidak Ok Ternary Operator")

// 3. Tanpa Ternari Operator
if(value){
    console.log("Ok Tanpa Ternary && Operator")
}

// 4. Menggunakan Ternary Operator
value && console.log("Ok Menggunakan Ternary && Operator")